import React, { Component } from 'react'



class AddCart extends Component {
    constructor(props) {
        super(props)
        this.state={
         counter:1,
          }
        this.plus = this.plus.bind(this)
        this.minus = this.minus.bind(this)

    }

    plus() {
        this.setState(Lcounter => ({
          counter: Lcounter.counter + 1
        }));
      }

    minus() {
      if(this.stat)
        this.setState(Lcounter => ({
            counter: Lcounter.counter - 1
          }));
    }




    render() {
        const {counter} = this.state;
        return (
            <div>
            <button onClick={this.plus}>+</button>              
             <p> {counter} </p> 
             <button onClick={this.minus} disabled={counter == 1}>-</button>
            <p> Add to cart </p>

        </div>        )
    }
}

export default AddCart;
